(1, 1, 'Un comentarista de WordPress', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2019-09-22 00:55:09', '2019-09-22 00:55:09', 'Hola, esto es un comentario.\nPara empezar a moderar, editar y borrar comentarios, por favor, visita la pantalla de comentarios en el escritorio.\nLos avatares de los comentaristas provienen de <a href=\"https://gravatar.com\">Gravatar</a>.', 0, '1', '', '', 0, 0),
(2, 182, 'Un comentarista de WordPress', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2019-07-07 18:00:23', '2019-07-07 18:00:23', 'Hola, esto es un comentario.\nPara empezar a moderar, editar y borrar comentarios, por favor, visita la pantalla de comentarios en el escritorio.\nLos avatares de los comentaristas provienen de <a href=\"https://gravatar.com\" rel=\"nofollow\">Gravatar</a>.', 0, '1', '', '', 0, 0),
(3, 182, 'alexis lujan', '14041291@itdurango.edumx', '', '::1', '2019-07-07 18:32:59', '2019-07-07 18:32:59', 'este es un comentario', 0, '1', '', '', 0, 0);
